import React,{useState} from 'react'

export default function AddForm() {
    const [form,setForm]=useState({})
    const [filename,setFilename]=useState('')
    const [content,setContent]=useState({})
    const handleChange=(e)=>{
        if(e.target.files){
            console.log(e.target.files[0].name)
        }else{
        console.log(e.target.value)
        }
    }

    const handleSubmit=(e)=>{
        e.preventDefault()
    }
    return (
        <div className="container">
            <div className="col-md-6 m-auto">
                <h5 className="text-primary text-center mb-2">Fill the Form</h5>
            <form onSubmit={handleSubmit} encType="multipart/form-data">
                <div className="form-group">
                    <label htmlFor="file" className="form-label">Choose your file</label>
                    <input type="file"  
                    id="file" 
                    name="file" 
                    className="form-control"
                    onChange={handleChange}
                    />
                </div>
                <div className="form-group mb-2">
                    <label htmlFor="name" className="form-label">Some Data</label>
                    <input type="text" 
                    name="name" 
                    id="name" 
                    className="form-control" 
                    placeholder="Enter Your Data"
                    onChange={handleChange}
                    />
                </div>
                <div className="form-group text-center">
                    <input type="submit" value="Submit" className="btn btn-outline-primary"/>
                </div>
            </form>
            </div>
            
        </div>
    )
}
