import React,{useContext} from 'react'
import {Context} from '../Context'

export default function Yaudios() {
    const {yaud}=useContext(Context)
    const [aud]=yaud
    console.log(aud)
    return (
        <div className="container-fluid">
            <div className="row justify-content-around mb-3">
                <h3 className="text-center text-primary m-3">Your Memories</h3>
                {
                    aud ?
                    aud.map((a,i)=>(
                        <div className="col-auto" key={i}>
                        <audio src={`data:audio/wav;base64,${Buffer.from(a.data).toString()}`}controls autoPlay></audio>
                        </div>
                    ))
                    :
                    <h5 className="text-center text-danger mt-5">Loading...</h5>
                }
            </div>
        </div>
    )
}
