import React,{useContext} from 'react'
import {Context} from '../Context'

export default function Yvideos() {
    const {yvid}=useContext(Context)
    const [vid,setVid]=yvid
    return (
        <div className="container-fluid">
            <div className="row justify-content-around">
            <h5 className="text-primary m-3 text-center">Your Memories</h5>
            {
                vid ?
                vid.map((v,i)=>(
                    <div className="col-auto">
                        <div className="card" style={{width:"30rem"}}>
                        <video src={`data:video/mp4;base64,${Buffer.from(v.data).toString()}`} controls></video>
                        </div>
                    </div>
                ))
                :
                <h5 className="text-danger m-3 text-center">Loading...</h5>
            }
            </div>
        </div>
    )
}
