import React, { useState } from 'react'
import axios from 'axios'
export default function Yimages() {
    const [img, setImg] = useState(() => {
        axios.get("http://localhost:5000/data/getimages")
            .then(res => {
                setImg(res.data)
                console.log(res.data)
            })
            .catch(err => console.log(err))
    });
    return (
        <div className="container-fluid">
            <div className="row justify-content-around">
                <h3 className="text-center text-primary m-3">Your Memories</h3>
                {
                    img ?
                    img.map((img,index)=>(
                        <div className="col-auto mb-3 bg-secondary p-1" key={index}>
                        <div className="card" style={{width:"18rem"}}>
                        <img src={`data:image/jpeg;base64,${Buffer.from(img.data).toString()}`} alt="yourphoto" className="card-img"/>
                        <div className="card-text">
                            {img.name}
                        </div>
                        </div>
                        </div>
                    ))
                    :
                    <span className="text-center text-danger">Loading...</span>
                }
            </div>

        </div>
    )
}
