import React, { useState } from 'react'
import axios from 'axios'

export default function Yimages() {
    const [images, setImages] = useState(() => {
        axios.get("http://localhost:5000/data/getimages")
            .then(res => setImages(res.data))
            .catch(err => console.log(err))
    });
    return (
        <div className="container-fluid">
            <div className="row">
                <div className="col-auto">
                    {
                        images ?
                            images.map((i, k) => (
                                <div className="card">
                                <img src={Buffer.from(i.data).toString()} className="card-img-top" alt="yourimage"/>
                                <div className="card-body">
                                  <h5 className="card-title">{i.name}</h5>
                                </div>
                              </div>
                    ))
                    :
                    <small className="text-center mt-5 text-danger">No images !</small>
                }
                </div>
            </div>
            <h4>images</h4>
        </div>
    )
}
